/* Breadcrumb Navigation */
.breadcrumbs {
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    gap: var(--space-1);
    padding: var(--space-2) var(--space-3);
    background: var(--bg-secondary);
    border: 1px solid var(--border-primary);
    border-radius: var(--radius-md);
    margin-bottom: var(--space-3);
    font-size: var(--text-sm);
    min-height: 36px;
}

.breadcrumb-item {
    color: var(--text-secondary);
    text-decoration: none;
    padding: var(--space-1) var(--space-2);
    border-radius: var(--radius-sm);
    transition: color var(--transition-fast), background var(--transition-fast);
    white-space: nowrap;
}

.breadcrumb-item:hover {
    color: var(--text-primary);
    background: var(--bg-tertiary);
}

.breadcrumb-item:focus {
    outline: 2px solid var(--border-focus);
    outline-offset: 1px;
}

.breadcrumb-item.active {
    color: var(--text-primary);
    font-weight: var(--font-semibold);
    cursor: default;
    background: var(--bg-elevated);
    border: 1px solid var(--border-primary);
}

.breadcrumb-item.active:hover {
    background: var(--bg-elevated);
}

.breadcrumb-separator {
    color: var(--text-muted);
    font-size: var(--text-lg);
    line-height: 1;
    user-select: none;
    flex-shrink: 0;
}

/* Nested Agent Indentation */
.nested-agent {
    margin-left: var(--space-4);
    border-left: 2px solid var(--agent-overlay-border);
    padding-left: var(--space-4);
}

.nested-agent .nested-agent {
    margin-left: var(--space-3);
}

.nested-agent .nested-agent .nested-agent {
    margin-left: var(--space-2);
}

/* Progressive visual hierarchy with depth indicator */
.subagent-overlay[data-depth="1"] {
    margin-left: var(--space-4);
}

.subagent-overlay[data-depth="2"] {
    margin-left: var(--space-8);
}

.subagent-overlay[data-depth="3"] {
    margin-left: var(--space-12);
}

/* Jump to Parent Button */
.jump-to-parent-btn {
    display: inline-flex;
    align-items: center;
    gap: var(--space-1);
    padding: var(--space-1) var(--space-2);
    font-size: var(--text-xs);
    font-weight: var(--font-medium);
    color: var(--text-secondary);
    background: transparent;
    border: 1px solid var(--border-secondary);
    border-radius: var(--radius-sm);
    cursor: pointer;
    transition: all var(--transition-fast);
    white-space: nowrap;
}

.jump-to-parent-btn:hover {
    color: var(--agent-overlay-accent);
    border-color: var(--agent-overlay-border);
    background: var(--agent-overlay-bg);
}

.jump-to-parent-btn:active {
    transform: scale(0.98);
}

.jump-to-parent-btn:focus {
    outline: 2px solid var(--border-focus);
    outline-offset: 1px;
}

.jump-to-parent-btn::before {
    content: '\2191'; /* Up arrow */
    font-size: var(--text-sm);
}

/* Agent Header with Jump Button */
.agent-header-controls {
    display: flex;
    align-items: center;
    gap: var(--space-2);
    margin-left: auto;
}

/* Navigation Highlight Animation */
.navigation-highlight {
    animation: navHighlight 2s ease-out;
}

@keyframes navHighlight {
    0% {
        box-shadow: 0 0 0 3px hsla(var(--purple-400), 0.6);
    }
    100% {
        box-shadow: 0 0 0 0 hsla(var(--purple-400), 0);
    }
}

/* Scroll Indicators */
.subagent-content {
    position: relative;
}

.subagent-content.has-scroll-top::before,
.subagent-content.has-scroll-bottom::after {
    content: '';
    position: sticky;
    display: block;
    left: 0;
    right: 0;
    height: 20px;
    pointer-events: none;
    z-index: 1;
}

.subagent-content.has-scroll-top::before {
    top: 0;
    background: linear-gradient(to bottom, var(--bg-primary), transparent);
}

.subagent-content.has-scroll-bottom::after {
    bottom: 0;
    background: linear-gradient(to top, var(--bg-primary), transparent);
}

/* Navigation Loading State */
.subagent-loading {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: var(--space-2);
    padding: var(--space-6);
    color: var(--text-secondary);
    font-style: italic;
}

.subagent-loading::before {
    content: '';
    width: 16px;
    height: 16px;
    border: 2px solid var(--border-secondary);
    border-top-color: var(--agent-overlay-accent);
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

@keyframes spin {
    to {
        transform: rotate(360deg);
    }
}

/* Navigation Error State */
.subagent-error {
    padding: var(--space-4);
    background: var(--color-error-bg);
    border: 1px solid var(--color-error-border);
    border-radius: var(--radius-md);
    color: var(--color-error);
    font-size: var(--text-sm);
}

/* Agent Container for Lazy Loading */
.agent-container {
    position: relative;
}

.agent-container[id] {
    scroll-margin-top: 100px; /* Account for sticky header */
}

/* Expanded Agent Visual Indicator */
.subagent-overlay.expanded {
    border-color: var(--agent-overlay-accent);
    box-shadow: var(--shadow-md);
}

.subagent-overlay.expanded .subagent-header {
    background: var(--agent-overlay-header);
    border-bottom: 1px solid var(--agent-overlay-border);
}

/* Back/Forward Navigation Hint */
.nav-hint {
    position: fixed;
    bottom: var(--space-4);
