   440→                        clearSearch();
   441→                    }
   442→                }
   443→                return;
   444→            }
   445→
   446→            // Enter in search box - navigate to next match
   447→            if (e.key === 'Enter') {
   448→                var searchBox = document.getElementById('search-box');
   449→                if (document.activeElement === searchBox) {
   450→                    e.preventDefault();
   451→                    if (e.shiftKey) {
   452→                        prevMatch();
   453→                    } else {
   454→                        nextMatch();
   455→                    }
   456→                }
   457→                return;
   458→            }
   459→        });
